import React from 'react';
import Axios from 'axios';
import {useState} from 'react';

function NovoCard(){

const [name, setName] = useState("");
const [image, setImage] = useState("");
const [number, setNumber] =useState("");
const [nome, setNome] =useState("");//ess é o nome obrigatorio que vai com a foto (no data.append())
// envia o nome do arquivo junto na requisição, bom pra identificar depois. segundo carinha não é neccessario mas é bpm
const adicionar =()=>{    


    const tudo ={
        name:name,
        number:number,
        nome:nome
    
    }

const data = new FormData();

data.append("imagePath", image);
data.append("nome", nome);

 
    
Axios.post("http://localhost:8080/user",{tudo,data} )
.then(res=>console.log(res)).catch(err=>console.log(err));



}

       return(
        <>
        <h2>Nome</h2>
        <input type="text" onChange={e=> setName(e.target.value)} />
        <h2>Images</h2>
        <input type="file" onChange={e=>setImage(e.target.value)} />
        <h2>Nome</h2>
        <input type="text" onChange={e=> setNome(e.target.value)} />
        <h2>Fone</h2>
        <input type="number" onChange={e=>setNumber(e.target.value)} />
        <button onClick={adicionar}> Adcionar</button>
        </>

    )

}

export default NovoCard;
