import React from 'react';
import Axios from 'axios';
import {useState} from 'react';

function NovoCard(){

const [name, setName] = useState("");
const [image, setImage] = useState(null);
const [number, setNumber] =useState("");
const [nome, setNome] =useState("");//ess é o nome obrigatorio que vai com a foto (no data.append())
// envia o nome do arquivo junto na requisição, bom pra identificar depois. segundo carinha não é neccessario mas é bpm
const adicionar  =(e)=>{    
   e.preventDefault();
/*const tudo ={
        name:name,
        number:number
         }*/
        
const data = new FormData();
data.append("imagePath", image);

Axios.post("http://localhost:8080/user",
{data}, 
{headers:{
    "Content-Type": `multipart/form-data; boundary=${data._boundary}`} })
.then(res=>console.log(res)).catch(err=>console.log(err));


}

       return(
        <>

        <form onSubmit={adicionar} enctype="multpart/form-data">
        <div className="form-group">
        <h2>Nome</h2>
        <input type="text" onChange={e=> setName(e.target.value)} />
        </div>
        <div className="form-group">
        <h2>Images</h2>
        <input type="file" onChange={e=>setImage(e.target.file)} />
        </div>
        <div className="form-group">
        <h2>Fone</h2>
        <input type="number" onChange={e=>setNumber(e.target.value)} />
        </div>
        <button type="submit"> Adcionar</button>
        </form>
        </>

    )

}

export default NovoCard;
