import React from 'react';
import Axios from 'axios';
import {useState} from 'react';
import Header from './Header';

function NovoCard(){

const [name, setName] = useState("");
const [ano, setAno] =useState(0);
const [desc, setDesc] =useState("");
const [image, setImage] = useState(null);//ess é o nome obrigatorio que vai com a foto (no data.append())
// envia o nome do arquivo junto na requisição, bom pra identificar depois. segundo carinha não é neccessario mas é bpm
const adicionar  =(e)=>{    
    e.preventDefault();
    /*const tudo ={
            name:name,
            number:number
            }*/
            
    const data = new FormData();
    data.append("name", name);
    data.append("ano", ano);
    data.append("desc",desc);

    for(var x = 0; x<image.length; x++) {
        data.append('imagePath', image[x])
    }

    Axios.post("http://localhost:8080/user",data )
    .then(res=>console.log(res)).catch(err=>console.log(err));
 }

       return(
        <>  
        
        <Header />
        <form onSubmit={adicionar} enctype="multpart/form-data">
        <div className="form-group">
        <h2>Nome</h2>
        <input type="text" onChange={e=> setName(e.target.value)} />
        </div>
        <div className="form-group">
        <h2>Images</h2>
        <input type="file" onChange={e=>setImage(e.target.files)} />
        </div>
        <div className="form-group">
        <h2>Ano</h2>
        <input type="number" onChange={e=>setAno(e.target.value)} />
        <h2>Descrição</h2>
        <input type="text" onChange={e=>setDesc(e.target.value)} />
        </div>
        <button type="submit"> Adcionar</button>
        </form>
        </>
    )
}
export default NovoCard;
